    #if CC_RENDER_MODE == 1
      velocity = cc_matWorld * velocity;
    #endif
  #endif
  #if ROTATION_OVER_TIME_MODULE_ENABLE
    vec3 rotTmp = a_texCoord2;
    float mulFactor = 1.0;
    if (rotTmp.x > 10.0 * 0.5) {
        rotTmp.x -= 10.0;
        mulFactor = -1.0;
    }
    vec4 rot = vec4(rotTmp, 0.0);
    rot.w = mulFactor * sqrt(1.0 - rot.x * rot.x - rot.y * rot.y - rot.z * rot.z);
