  #if CC_RENDER_MODE != 4
    vec2 cornerOffset = vec2((a_texCoord.xy - 0.5));
    #if CC_RENDER_MODE == 0 || CC_RENDER_MODE == 3
      computeVertPos(pos, cornerOffset, rot, compScale, cc_matViewInv);
